import { Component } from 'react';
import { createStore } from 'redux';

const redux = require('redux')

const boardStates = {board:true,days:0}

const Reducer = (state=boardStates,action) => {
    if(action.type == "increase"){
        if(action.amount){
            return({days:state.days+ action.amount,board:state.board});
        }
        else{
            return({days:state.days+1,board:state.board});
        }
    }
    if(action.type == "decrease"){
        if(state.days>0){
            return({days:state.days-1,board:state.board});
        }

    }
    if(action.type == "toggle"){
        return({days:state.days,board:!state.board})
    }
}


const ReduxCentralStore = redux.createStore(Reducer);

const Components = () => {
    const initialState = ReduxCentralStore.getState()
    console.log(initialState);
}

ReduxCentralStore.subscribe(Components)
ReduxCentralStore.dispatch({type:"increase"})


export default ReduxCentralStore;
