import React,{useState,useEffect} from 'react'
import AxiosRequest from '../../axios'
import Search from '../basic/search'
import { DecodeToken } from '../token_decode'
import Card from './Card'

const user_id = DecodeToken(localStorage.getItem("access_token")).user_id

const Contact = () => {

    const [search,setSearch] = useState();
    const [contacts,setContacts] = useState([]);

    const RetrieveContacts = async() => {
        const data = await AxiosRequest.get(`/accounts/profile/${user_id}`)
                    .then((res) => {
                      const {fr,fg} = res.data
                      for (each in fr){
                        if(each in fg){
                          contacts.push(each);
                        }
                      }

                      setContacts(res.data.fg.concat(res.data.fr))
                    })
                    .catch((err) => {console.log(err)})
    }


    const RenderContacts = contacts.map((each) => {
     return(
      <div>
        <Card props={each}></Card>
      </div>
     );

    })


    useEffect(() => {
      RetrieveContacts();

    }, [])


    return (
    <div className='container mt-4 ml-4' text>
    <div className='d-flex' style={{justifyContent:"center",gap:"10%"}}>
    <p style={{fontSize:"25px"}}>MyContacts</p>
    <Search/>
    </div>

    <div className='col-md-5'>
    {RenderContacts}
    </div>
    </div>


  )
}

export default Contact
