import React,{useState} from 'react';
import { useNavigate } from 'react-router';

const Create = () => {
  const [data,setData] = useState("");
  const [content,setContent] = useState("");
  const [title,setTitle] = useState("");


  const navigate = useNavigate();
  const handleSubmit = () =>{
    if(data!=="" && content !== "" && title!==""){
      const newPostData = {data:{data},content:{content},"title":{title}}
      console.log(newPostData);
      navigate("/posts");
    }
    else{
      alert("Fill all the credentials");
    }
  }


  const handleClear = () => {
    alert("cleared");
  }





  return(

  <div className='container col-md-6'>
  <h1 className=''>Create a new Post</h1>
  <div class="form-group" style={{marginTop:"2%"}}>
    <p className='lead'><b>Title</b></p>
    <input type="text" class="form-control" placeholder="Title" onChange={(e) => {setTitle(e.target.value)}}/>
  </div>
  <div class="form-group" style={{marginTop:"2%"}}>
  <p className='lead'><b>Content</b></p>
    <textarea type="text" class="form-control" rows="4" placeholder="Content" onChange={(e) => {setContent(e.target.value)}}/>
  </div>
  <div class="form-group" style={{marginTop:"2%"}}>
  <p className='lead'><b>Data</b></p>
    <textarea type="text" class="form-control" rows="8" placeholder="Data" onChange={(e) => {setData(e.target.value)}}/>
  </div>
  <div align="right" style={{marginTop:"3%"}}>
  <i class="red check icon" onClick={handleSubmit}></i>
  <i class="x icon" onClick={handleClear}></i>
  </div>



  </div>);
};

export default Create;
