import React, { useEffect } from 'react'
import { useState } from 'react';

const FORMKEY = "form";


const UpdateNote = (props) => {

    const {topic,data,writer,id} = props.passData;


    const detailObject = {
        "topic":topic,
        "data":data,
        "writer":writer,
        "id":id,
    }

    const [formData,setFormData] = useState(detailObject);


    // const storedForm = JSON.parse(localStorage.getItem("form"));
    // if(storedForm){
    //     detailObject.topic =storedForm.topic;
    //     detailObject.data =storedForm.data;
    //     detailObject.writer =storedForm.writer;
    // }






    useEffect(() => { 
       console.log("start");
       localStorage.setItem(FORMKEY,JSON.stringify(detailObject));
    }, [])





    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(formData);

    }

    const handleClear = (event) => {
        setFormData(detailObject);
        event.preventDefault();
        console.log(formData);
    }


    return (
        <div className="ui main">
        <form style={{margin:"20px",marginLeft:"20%",marginRight:"20%"}}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Update the Note</h2></div>
            <div className="ui form">
            <div className="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" value={formData.topic} onChange={(e) =>{detailObject.topic=e.target.value;localStorage.setItem("form",detailObject) ;setFormData(detailObject);console.log(formData)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea value={formData.data} onChange={(e) =>{detailObject.data=e.target.value;localStorage.setItem("form",detailObject);setFormData(detailObject);console.log(formData)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" value={formData.writer} onChange={(e) =>{detailObject.writer=e.target.value;localStorage.setItem("form",detailObject);setFormData(detailObject);console.log(formData)}}></textarea>
            </div>
            </div>
            <button className="ui button black" onClick={handleClear} >Clear</button>
            <button className="ui button black" onClick={handleSubmit}>Submit</button>
        </form>
        </div>
    );

}
export default UpdateNote;
