import { useState,useContext,useRef } from "react";
import React from 'react';
import Cart from "./cart";
import { CartContext } from "../../app";






const MealList = (props) => {
    const cartInputRefValue = useRef()
    const {setCartValue,cartValue} = useContext(CartContext)
    const [cartInput,setCartInput] = useState({price:0,num:0});

    const handleAdd = () => {
        if(cartInput.num != 0){
            const a = cartInputRefValue.current.value
            console.log(a)
            setCartValue(parseInt(cartValue)+parseInt(cartInput.num))
            setCartInput({price:0,num:0});

        }
        else{
            alert('value cant be empty....')
        }
    }

    const handleChange = (e) => {
        if(e.target.value != 0 || e.target.value != null){
            setCartInput({price:cartInput.price,num:e.target.value})
        } 
    }


    const RenderMealList = props.passMealDetails.map((each) => {
        return(
            <div key={each.id} style={{display:"flex",justifyContent:"space-between",alignItems:"center"}} className='mb-4'>
                 <div>
                    <h4>{each.name}</h4>
                    <p>Price: {each.price}</p>
                 </div>
                 <div className='d-flex'>
                    <input ref={cartInputRefValue} value={cartInput} class="form-control me-2" type='number' style={{width:"100px",borderRadius:"20px"}} placeholder="Add to Cart" onChange={handleChange}/>
                    <button className='btn btn-dark' onClick={handleAdd}>Add</button>
                 </div>
            </div> 
        );
    });

    return(
        <div style={{ borderRadius:"25px",marginTop:"3%",backgroundColor:"lightblue",padding:"2%"}} className='container col-md-6'>
           <div align='center' style={{padding:"2%"}}>
            <h1  className='display-5'>Meal List</h1>
           </div>
           {RenderMealList}
        </div>
    );
};


export default MealList;
