import React,{useState,useEffect} from 'react'
import { useNavigate, useLocation } from 'react-router'
import { Link } from 'react-router-dom'
import { DecodeToken } from '../token_decode'
import AxiosRequest from '../../axios'
import { Axios } from 'axios'


const Chat = () => {
    const location = useLocation()
    const [message,setMessage] = useState("")
    const [messageData,setMessageData] = useState([])
    const user_id = DecodeToken(localStorage.getItem("access_token"))
    const chat_id = location.pathname.slice(14)
    const yy =location.pathname.slice(14)
    const ws_api = new WebSocket(`ws://localhost:8000/ws/chat/${chat_id}`)

    ws_api.onopen = () => {console.log("connection established")}

    ws_api.onclose = () => {console.log("disconnected")}


    ws_api.onmessage = (res) => {
        const data = JSON.parse(res.data)

        if(data.status === "success"){
            console.log(res.data)
            retrieveMessages();
        }

    }

    const socketMessages = () => {
        const msg_data = JSON.stringify({"data":message,"code":chat_id})
        if(msg_data.data !== ""){

            ws_api.send(msg_data);
        }
    }







    const retrieveMessages = async() => {
        const getMessage = await AxiosRequest(`/chat/text/${chat_id}`)
                            .then((res) => {
                                setMessageData(res.data);
                            })
    }

    const sendMessages = async() => {
        const sendMessage = await AxiosRequest.post(`/chat/text/${chat_id}`,{"data":message})
                            .then((res) => {retrieveMessages();})

    }

    const RenderMessages = messageData.map((each) => {
        return(
            <div className='mt-3 mb-3'>

                <p className='lead'>{each.sender_name}: {each.data}</p>
            </div>
        )
    })

    useEffect(() => {
        retrieveMessages();
    },[])



    return (
    <div className='container-fluid mt-4 ml-4'>
        <div className='d-flex'>
            <input placeholder='Enter the message' onChange={(e) => {setMessage(e.target.value)}}></input>
            {/* <button className='btn btn-dark' onClick={sendMessages}>Send</button> */}
            <button className='btn btn-dark' onClick={socketMessages}>Send(via ws)</button>
        </div>
        {RenderMessages}
    </div>
  )
}

export default Chat;
