import React,{useState} from 'react'
import { useNavigate } from 'react-router'

const CreateNote = (props) => {

    let [about,setAbout] = useState("")
    let [note,setNote] = useState("")
    let [author,setAuthor] = useState("")

    let navigate = useNavigate;
    console.log(navigate);


    const handleClear = (event) => {
        event.preventDefault();
        setAbout("");
        setNote("");
        setAuthor("");

    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if(about !== "" && note !== "" && author !== ""){

            let formData = {
                "topic":about,
                "data":note,
                "writer":author
            }

            props.passNote(formData);
            navigate
        }
        else{
            alert("All the fields must be filled");
        }
    }








    return (
        <div className="ui main">
        <form style={{margin:"20px",marginLeft:"20%",marginRight:"20%"}} onSubmit={handleSubmit}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Create a New Note</h2></div>
            <div className="ui form">
            <div className="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" value={about} onChange={ (event) => { setAbout(event.target.value)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea value={note} onChange={ (event) => { setNote(event.target.value)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" value={author} onChange={ (event) => { setAuthor(event.target.value)}}></textarea>
            </div>
            </div>
            <button className="ui button black" type="submit" onClick={handleClear}>Clear</button>
            <button className="ui button black" type="submit" onClick={handleSubmit}>Submit</button>


        </form>
        </div>
    )
}

export default CreateNote;
