import classes from './Counter.module.css';
import { useDispatch, useSelector } from 'react-redux';


const ReduxCounter = () => {
  const CounterHandler = () => {};

  const CounterData = useSelector(state => state.days)
  const Dispatch =  useDispatch()
  console.log(CounterData)






  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{CounterData}</div>
      <div className='d-flex justify-center gap-1'>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase"})}}>Increase</button>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase",amount:5})}}>Increase1</button>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase",amount:10})}}>Increase2</button>
      <button className='btn btn-dark' onClick={() => {if(CounterData>0){Dispatch({type:"decrease"})}}}>Decrease</button>
      <button className='btn btn-dark'>Operate</button>
      </div>
    </main>
  );
};

export default ReduxCounter;
