import React,{ useState } from 'react'
import { Link,useNavigate } from 'react-router-dom';

const Login = () => {

    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");
    const [logState,setLogState] = useState(false);
    const [pass,setPass]  = useState(true);


    const Lock = () => {
        return(
            <div class="ui icon warning message">
                <i class="lock icon"></i>
                <div class="content">
                    <div class="header">
                    Login failed!
                    </div>
                    <p>You might have misspelled your username or password!</p>
                </div>
                </div>

        );
    }


    let navigate = useNavigate();
    const handleLogin = (e) => {
        e.preventDefault();
        if(localStorage.getItem(username)===password){
            setLogState(true);
            navigate("/list");
        }
        else{
            console.log(pass);
            setPass(false);
        }
    }









    return (
        <div>
        <div class="ui top attached tabular menu">
  <a class="item">
    Bio
  </a>
  <a class="item active">
    Photos
  </a>
  <div class="right menu">
    <div class="item">
      <div class="ui transparent icon input">
        <input type="text" placeholder="Search users..." autocomplete="off"/>
        <i class="search link icon"></i>
      </div>
    </div>
  </div>
</div>
<div class="ui bottom attached segment">
  <p></p>
</div>
</div>
    )
}

export default Login;
<div align="center">
        <div class="page-login">
        <div class="ui centered grid container">
            <div class="nine wide column">
            {!pass && <Lock/>}
            <div class="ui fluid card">
                <div class="content">
                <form class="ui form">
                <div class="field">
                    <label>Username</label>
                    <input type="text" name="user" placeholder="User" onChange={(e) => {setUsername(e.target.value)}}/>
                </div>
                <div class="field">
                    <label>Password</label>
                    <input type="password" name="pass" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}}/>
                </div>
                <button class="ui primary labeled icon button" onClick={handleLogin}>
                    <i class="unlock alternate icon"></i>
                    Login
                </button>
                </form>
                </div>
            </div>
            </div>
        </div>
        </div>
        </div>
