import React,{useState} from 'react';
import { useNavigate } from 'react-router';
import AxiosRequest from '../../axios';
import axios from '../../axios';



const Create = () => {
  const [newPostDetails,setNewPostDetails] = useState({title:"",content:"",data:""})



  const navigate = useNavigate();

  const handleSubmit = () =>{
    if(newPostDetails.data!=="" && newPostDetails.content !== "" && newPostDetails.title!==""){
      const newPostData = JSON.stringify({title:newPostDetails.title,content:newPostDetails.content,data:newPostDetails.data})
      const a = AxiosRequest.post('/shareme/posts/new',newPostData)
                .then((res) => { console.log("successfully")})
    }
    else{
      alert("Fill all the credentials");
    }
  }


  const handleClear = () => {
    newPostDetails.title()
    setNewPostDetails({title:"",content:"",data:""});
    alert("cleared");
  }





  return(

  <div className='container col-md-6'>
  <h1 className=''>Create a new Post</h1>
  <div class="form-group" style={{marginTop:"2%"}}>
    <p className='lead'><b>Title</b></p>
    <input type="text" defaultValue={newPostDetails.title} class="form-control" placeholder="Title" onChange={(e) => { setNewPostDetails({data:newPostDetails.data,content:newPostDetails.content,title:e.target.value})}}/>
  </div>
  <div class="form-group" style={{marginTop:"2%"}}>
  <p className='lead'><b>Content</b></p>
    <textarea defaultValue={newPostDetails.content}  type="text" class="form-control" rows="4" placeholder="Content" onChange={(e) => { setNewPostDetails({data:newPostDetails.data,content:e.target.value,title:newPostDetails.title})}}/>
  </div>
  <div class="form-group" style={{marginTop:"2%"}}>
  <p className='lead'><b>Data</b></p>
    <textarea defaultValue={newPostDetails.data} type="text" class="form-control" rows="8" placeholder="Data" onChange={(e) => { setNewPostDetails({data:e.target.value,content:newPostDetails.content,title:newPostDetails.title})}}/>
  </div>
  <div align="right" style={{marginTop:"3%"}}>
  <i class="red check icon" onClick={handleSubmit}></i>
  <i class="x icon" onClick={handleClear}></i>
  </div>
  </div>);
};

export default Create;
