import React,{useState} from 'react'

const CreateNote = () => {
    let [about,setAbout] = useState("")
    let [note,setNote] = useState("")
    let [author,setAuthor] = useState("")



    const handleData = () => {
        let formData = {
            "topic":about
        }
    }

    const handleClear = () => {
        setAbout("");
        setNote("");
        setAuthor("");
    }








    return (
        <div style={{margin:"20%"}}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Create a new Note</h2></div>
            <div class="ui form">
            <div class="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" onChange={ (event) => { setAbout(event.target.value)}}></textarea>
            </div>
            <div class="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea onChange={ (event) => { setNote(event.target.value)}}></textarea>
            </div>
            <div class="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" onChange={ (event) => { setAuthor(event.target.value)}}></textarea>
            </div>
            </div>
            <button class="ui button black" type="submit" onClick={handleClear}>Clear</button>
            <button class="ui button black" type="submit" onClick={handleData}>Submit</button>


        </div>
    )
}

export default CreateNote;
