import React,{useState,useReducer} from 'react'

const HomeApp = () => {


  const reducer = (state,action) => {
    if(action.type === "increment"){
      return {count: state.count+1,displayText:state.displayText}
    }  
    if(action.type === "decrement"){
      return {count: state.count-1,displayText:state.displayText}
    }
    if(action.type === "toggletext"){
      return {count: state.count,displayText:!state.displayText}
    }
  }


  const [state,dispatch] = useReducer(reducer, {count:0,displayText:true})


  const Odd = () =>{
    return(<div>
      <h3 className='display-5'>Odd Page</h3>
    </div>)
  }

  const Even = () =>{
    return(<div>
      <h3 className='display-5'>Even Page</h3>
    </div>)
  }


  return (


    <div className='container col-md-4 mt-4'>
        {state.displayText ? <Even/> : <Odd/>}
        <h2 className='display-6'>Clicks: {state.count}</h2>
        <div className='d-flex mt-4 cursor-not-allowed'>
        <button className='btn btn-dark' onClick={() => {dispatch({type:"increment"}); dispatch({type:"toggletext"})}}>Next Page</button>
        <button className='btn btn-dark' onClick={() => {dispatch({type:"decrement"}); dispatch({type:"toggletext"})}}>Back Page</button>
        </div>
    </div>

  )
}

export default HomeApp
