import React, { useState,useEffect } from 'react';
import { useLocation,useNavigate } from 'react-router';
import { Link } from 'react-router-dom';
import Loading from '../basic/loading';
import { PostContext } from '../../app';
import { useContext } from 'react';
import AxiosRequest from '../../axios';
import ReviewMangement from './details/comments';
import Operations from './details/operations';
import { decodeToken } from '../token_decode';


const Details = () => {

    const navigate = useNavigate();
    const location = useLocation();
    const key = parseInt(location.pathname.slice(15,))

    const {postDetails,setPostDetails} = useContext(PostContext)
    const {details,loading} = postDetails



    const retrievePostDetails = async() => {
        const fetchDetails = await fetch(`http://localhost:8000/shareme/posts/${key}`)
        const cleanFetchDetails = await fetchDetails.json();
        setPostDetails({loading:false,details:cleanFetchDetails});
    }

    const LikePosts = async() => {
        console.log("hello")

    //     const user_id = decodeToken(localStorage.getItem("access_token"))
    //     const comment_like = AxiosRequest.post(`/shareme/posts/${key}/likes`,{"user_id":user_id})
    }



    useEffect(() => {
        retrievePostDetails();
    }, []);


    if(postDetails.loading ===false){
    return(  
    <div className='container' align="center">
    <div className='col-md-8' style={{textAlign:"left",margin:"3%"}}>
        <div>
            <Operations/>


        <div className='d-flex' style={{alignItems:"center"}}>
        <h1 className="display-4">{postDetails.details.title}</h1>
        <i style={{marginLeft:"25px"}} class="big thumbs up red icon" onClick={LikePosts}></i>
        <p style={{fontSize:"20px"}}>14</p>
        </div>
        </div>
        <div style={{margin:"3%"}}>
        <h3>{postDetails.details.content}</h3>
        </div>

        <div style={{margin:"3%",fontSize:"125%"}}>
        <p>{postDetails.details.data}</p>
        <Link to={`/shareme/profile/${postDetails.details.author}`}><p className='lead'>{postDetails.details.username}</p></Link>
        </div>

        <div>
        <ReviewMangement/>
        </div>

    </div>
    </div>
  );}
  else{
      return(
          <div><Loading/></div>
      )
  }

};

export default Details;
