import React from 'react'
import { useState,useEffect } from 'react';
import Card from '../posts/card';
import Details from '../posts/details';

const Home = () => {

    const [postData,setPostData] = useState({loading:true,posts:[]});

    const GetAPIData = async() => {
        const rawData = await fetch("http://localhost:8000/shareme/posts");
        const jsonData = await rawData.json()
        setPostData({loading:false,posts:jsonData});
    }

    useEffect(() => {
        // GetAPIData();   
    },[]);


    const RenderPostListData = postData.posts.map((each) => {
        return(
            <div key={each.id} style={{margin:"4%"}}>
                <Card CardViewData={each}/>
            </div>
        );
    })
    if(postData.loading === false){
    return (
        <div align="center" className='container' style={{"display":"grid","gridTemplateColumns": "repeat(auto-fit, minmax(200px, 1fr))"}}>
            {RenderPostListData}
        </div>
    )
    }
    else{
        return(
            <div style={{display:"flex",justifyContent:"center",alignItems:"center",margin:"10%"}}>
                <div><h1>Loading Posts.......</h1></div>
                <div class="spinner-grow text-primary" role="status">
                    <span class="sr-only"></span>
                </div>
                <div class="spinner-grow text-primary" role="status">
                    <span class="sr-only"></span>
                </div>
            </div>
        );
    }
}

export default Home;
