import React from 'react'
import img1 from '../Images/img1.jpg'
import img2 from '../Images/img2.jpg'
import img3 from '../Images/img3.png'
import { useNavigate } from 'react-router'
import { useState } from 'react'
import {MovieAPIKey} from '../../API/MovieAPIKey'
import MovieAPI from '../../API/MovieAPI'

const MovieForm = (props) => {

    const navigate = useNavigate()
    const [search,setSearch] = useState("")
    const [movieListArray,setMovieListArray] = useState([]);
    const [task,settask] = useState(0);


    const getMoviesList = async() => {

        const movieList = await MovieAPI.get(`?apikey=${MovieAPIKey}&type=movie&s=${search}`);
        if (movieList){
            const movieScanResults = await movieList.data.Search
            if(movieScanResults){
                movieScanResults.map((each) => {
                    movieListArray.push(each);
                })
                setMovieListArray(movieListArray);
                settask(1);
            }
        }

    }



    const handleMovieSearch = (event) => {
        event.preventDefault();

        if (search != ""){
            getMoviesList();
            if(task===1){
                console.log("passed");
                props.passSearchData(movieListArray);

            }
        }
        navigate("/movies/list");  
    }






    return (
        <div>
             <div style={{marginTop:"20px",alignItems:"center",textAlign:"center"}}>
            <h1>Search for Movies</h1>
            <input onChange={(e)=> {setSearch(e.target.value)}}/>
            <button class="ui button secondary" onClick={handleMovieSearch}>Search</button>
        </div>
        <div id="carouselExampleIndicators" class="carousel slide" data-bs-ride="carousel">
        <div class="carousel-indicators">
            <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="0" class="active" aria-current="true" aria-label="Slide 1"></button>
            <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="1" aria-label="Slide 2"></button>
            <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="2" aria-label="Slide 3"></button>
        </div>
        <div class="carousel-inner">
            <div class="carousel-item active">
            <img src={img1} class="d-block w-100" alt="..."/>
            </div>
            <div class="carousel-item">
            <img src={img2} class="d-block w-100" alt="..."/>
            </div>
            <div class="carousel-item">
            <img src={img3} class="d-block w-100" alt="..."/>
            </div>
        </div>
        <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
            <span class="carousel-control-prev-icon" aria-hidden="true"></span>
            <span class="visually-hidden">Previous</span>
        </button>
        <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
            <span class="carousel-control-next-icon" aria-hidden="true"></span>
            <span class="visually-hidden">Next</span>
        </button>
        </div>




        </div>

)
}

export default MovieForm;
