import React,{createContext, useContext,useState} from 'react'
import Login from './Components/basic/login';
import Logout from './Components/basic/logout';
import Home from './Components/basic/home';
import Signup from './Components/basic/signup';
import {BrowserRouter as Router,Routes,Route} from 'react-router-dom'
import Header from './Components/basic/header';
import Details from './Components/posts/details';
import Create from './Components/posts/create';
import MealsHome from './Components/meals/MealsHome';
import UpdatePost from './Components/posts/update';
export const CartContext = createContext(null);
export const PostContext = createContext(null);


const App = () => {
    const [cartValue,setCartValue] = useState(0);
    const [postDetails] = useState({loading:true,details:[]});


    return (
        <div>
        <CartContext.Provider value={{cartValue,setCartValue}}>
        <PostContext.Provider value={{postDetails,setCartValue}}>

        <Router>
            <Header/>
            <Routes>
                <Route path="/login" element={<Login></Login>}/>
                <Route path="/logout" element={<Logout></Logout>}/>
                <Route path="/signup" element={<Signup></Signup>}/>

                <Route path="/shareme/posts" element={<Home></Home>}/>
                <Route path="/shareme/posts/create" element={<Create></Create>}/>
                <Route path="/shareme/posts/:id" element={<Details/>}/>
                <Route path="/shareme/posts/:id/update" element={<UpdatePost/>}/>

                <Route path='/meals' element={<MealsHome/>}/>
            </Routes>

        </Router>
        </PostContext.Provider>
        </CartContext.Provider>


        </div>

    )
}

export default App;
