import React,{useState,useEffect} from 'react';
import { useLocation } from 'react-router';
import AxiosRequest from '../../axios';
const UpdatePost = (props) => {

    const [formData,setFormData] = useState({title:"",content:"",data:""});

    useEffect(() => {

    const getFormData = async() => {
      const a = await fetch("http://localhost:8000/shareme/posts/1")
      const b = await a.json()
      setFormData({title:b.title,data:b.data,content:b.data})
    }
    getFormData()
    }, []);



    const {title,data,content} = formData

    const handleSubmit = () => {
        const cleanFormData = JSON.stringify(formData);
        AxiosRequest.post('/shareme/posts/update');    
    }

    return(
        <div className='container col-md-6 mt-4'>

            <div className='mb-4'>
                <h1 className='display-4'>Update Post</h1>
            </div>

            <div>
            <div class="mb-3 mt-3">
            <label class="form-label lead">Title</label>
            <input type="text" defaultValue={title} class="form-control" placeholder="Enter the title" onChange={(e) => {setFormData({title:e.target.value,content:content,data:data});console.log(formData)}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Content</label>
            <textarea class="form-control"  defaultValue={content} rows="3" placeholder="Enter the content" onChange={(e) => {setFormData({title:title,content:e.target.value,data:data})}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Data</label>
            <textarea class="form-control" defaultValue={data} rows="3" placeholder="Enter the data" onChange={(e) => {setFormData({title:title,content:content,data:e.target.value})}}/>
            </div>
            <div className='d-grid gap-2'>
            <button className='btn btn-dark' onClick={handleSubmit}>Submit</button>
            </div>

            </div>

        </div>
    );
};

export default UpdatePost;
