import React,{useState,useEffect} from 'react';
import { useLocation,useNavigate } from 'react-router';
import AxiosRequest from '../../axios';
import { PostContext } from '../../app';
import { useContext } from 'react';


const UpdatePost = (props) => {
    const navigation = useNavigate()
    const {postDetails,setPostDetails} = useContext(PostContext);
    const {title,data,content,id} = postDetails.details

    const [formData,setFormData] = useState({title:"",content:"",data:""});

    const handleSubmit = async() => {

        postDetails.details.title = formData.title
        postDetails.details.data = formData.data
        postDetails.details.content = formData.content

        setPostDetails({loading:false,details:postDetails.details})
        const cleanFormData = JSON.stringify(postDetails.details);

        const req  = await AxiosRequest.put(`/shareme/posts/${id}/update`,cleanFormData)
        .then((res) => {navigation(`/shareme/posts/${id}`)})
    }

    const handleUpdate = () => {

    }

    return(
        <div className='container-fluid col-md-5 mt-4' style={{backgroundColor:"white",padding:"3%",borderRadius:"25px",marginTop:"25px"}}>

            <div className='mb-4'>
                <h1 className='display-4'>Update Post</h1>
            </div>

            <div>
            <div class="mb-3 mt-3">
            <label class="form-label lead">Title</label>
            <input type="text" defaultValue={title} class="form-control" placeholder="Enter the title" onChange={(e) => {setFormData({title:e.target.value,content:content,data:data})}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Content</label>
            <textarea class="form-control"  defaultValue={content} rows="3" placeholder="Enter the content" onChange={(e) => {setFormData({title:title,content:e.target.value,data:data})}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Data</label>
            <textarea class="form-control" defaultValue={data} rows="3" placeholder="Enter the data" onChange={(e) => {}}/>
            </div>
            <div className='d-grid gap-2'>
            <button className='btn btn-dark' onClick={handleSubmit}>Submit</button>
            </div>

            </div>

        </div>
    );
};

export default UpdatePost;
