import React, { useState,useEffect } from 'react';
import { useLocation } from 'react-router';
import { Link } from 'react-router-dom';


const Details = () => {
    const location = useLocation();
    const [postDetails,setPostDetails] = useState([]);
    const postID = location.state.key.id;
    if(location.state.key.id){


        const retrievePostDetails = async() => {
            const fetchDetails =await  fetch(`http://localhost:8000/shareme/posts/${postID}`)
            const cleanFetchDetails = await fetchDetails.json();
            setPostDetails(cleanFetchDetails);
            console.log(postDetails);
            console.log(cleanFetchDetails);
        }

        useEffect(() => {
            retrievePostDetails();
        }, []);

        return(  
        <div className='container' align="center">
        <div className='col-md-6' style={{textAlign:"left",margin:"3%"}}>
            <div>
            <Link to="delete"><i class="red trash icon"></i></Link>
            <Link to="update"><i class="red edit icon"></i></Link>
            <h1>{postDetails.title}</h1>
            </div>
            <h4>{postDetails.content}</h4>
            <p>{postDetails.data}</p>
        </div>
        </div>
    );
    }

    else{
        return(
            <div><h1>No Data to display</h1></div>
        );
    }
};

export default Details;
