import React,{useState,useEffect} from 'react'
import { useLocation, useNavigate } from 'react-router';
import AxiosRequest from '../../axios';
import { DecodeToken } from '../token_decode';
import { Link } from 'react-router-dom';
import { Navigate } from 'react-router';

const Profile = () => {
  const navigation = useNavigate()
  const location = useLocation();
  let key =window.location.pathname.slice(17,)
  const {user_id} = DecodeToken(localStorage.getItem("access_token"))
  const [followData,setFollowData] = useState({following:[],followers:[]})





  const [profile,setProfile] = useState({about:"",id:"",adress:"",birthdate:"",followers:"",following:"",hobby:"",name:"",location:"",status:"",user:"",work:"",image:"",email:"",website:"",phone:""});


  const HandleUserNavigation = (idd) => {
      console.log(idd);
      navigation(`/shareme/profile/${idd}`);
      retrieveProfileDetails(idd);
  }


  const retrieveProfileDetails = async(idd) => {
    if(idd){
      key =idd
    }
    const details =await AxiosRequest.get(`/accounts/profile/${key}`)
                    .then((res) => {

                      const {phone,image,about,id,adress,birthdate,followers,following,hobby,name,location,status,user,work,email,website} = res.data.ser_data
                      setProfile({phone:phone,email:email,website:website,about:about,id:id,adress:adress,birthdate:birthdate,followers:followers,following:following,hobby:hobby,name:name,location:location,status:status,user:user,work:work,image:image});
                      setFollowData({following:res.data.fg,followers:res.data.fr})

                    })
                    .catch(err => console.log(err))
  }

  useEffect(() => {
    retrieveProfileDetails()
  },[])


  const handleFollowers = () => {
    const followers = AxiosRequest.put(`/accounts/profile/${key}`,{user_id:user_id,fol_id:key})
                      .then((res) => {console.log(res);retrieveProfileDetails()})
                      .catch((err) => {console.log(err)})
  }


  const ProfileButton = () => {
    if(profile.user != user_id){
      return(
            <div className='d-flex mt-4' style={{gap:"15px"}}>

            <button className='btn btn-dark'>
              <div style={{gap:"4px"}} className="d-flex" onClick={() => {navigation(`/shareme/text/${user_id+"-"+profile.user}`)}}>
                <div>
                Text
                </div>
                {chat}
              </div>
            </button>

            <button className='btn btn-dark'>
              <div style={{gap:"4px"}} className="d-flex"  onClick={handleFollowers}>
                <div>
                Follow
                </div>
                {follow}
              </div>
            </button>

            <button className='btn btn-dark'>
              <div style={{gap:"4px"}} className="d-flex">
                <div>
                Call
                </div>
                {call}
              </div>
            </button>
            </div>

            );
    }
    else{
      return(null);
    }  
  }

  const RenderFollowers = followData.followers.map((each) => {
    const k = each[1]
    return(
      <div key={each[1]}>

      <p className='lead' onClick={() => {HandleUserNavigation(k)}}> name: {each[0]}, id: {each[1]}  </p>

      </div>
    );
  })


  const RenderFollowing = followData.following.map((each) => {
    const k = each[1]
    return(
      <div key={each[1]}>
      <p className='lead' onClick={() => {HandleUserNavigation(k)}}> name: {each[0]}, id: {each[1]}  </p>

      </div>
    );
  })


  const text_id = user_id+"-"+profile.user;

  const chat = <div className='ml-3'><i className='large wechat white icon'></i></div>
  const call = <i className='large phone white icon'></i>
  const follow = <div className='d-flex'>
     <i className='large user user white icon'></i>
     <i className='large user thumbs up  icon'></i>
  </div>








  return (
    <div className='container row mt-5 ml-5 mb-5'>
      <div className='col-md-3 mb-5' >
      <img  class="img-fluid" style={{width:"200px",height:"200px"}} src={`http://localhost:8000${profile.image}`}></img>

      <p style={{fontSize:"20px"}} className="mt-3">Adress:</p>
      <p className='mt-1 lead' style={{color:"maroon"}}>{profile.adress}</p>

      <p style={{fontSize:"20px"}} className="mt-3">Email:</p>
      <p className="mt-1 lead" style={{color:"maroon"}}>{profile.email}</p>

      <p style={{fontSize:"20px"}} className="mt-3">Website:</p>
      <p className="mt-1 lead" style={{color:"maroon"}}>{profile.website}</p>

      <p style={{fontSize:"20px"}} className="mt-3">Phone:</p>
      <p className="mt-1 lead" style={{color:"maroon"}}>{profile.phone}</p>
      </div>




    <div className='col-md-6'>
      <div>
      <div className='d-flex' style={{alignItems:"center",gap:"20px"}}>

      <p className='lead' style={{fontSize:"30px"}}>{profile.name}</p>
      <div className='d-flex' style={{alignItems:"center"}}>
      <i className="large map signs icon"></i>
      <p style={{fontSize:"15px",color:"gray"}}>{profile.location}</p>
      </div>
      </div>
      </div>

      <div className='d-flex mt-2 mb-2' style={{gap:"20px"}} >
      <p style={{fontWeight:"40px",fontSize:"18px"}}>Followers: {profile.followers.length}</p>
      <p style={{fontWeight:"40px",fontSize:"18px"}}>Following: {profile.following.length}</p>
      </div>

      <p className='lead' style={{color:"maroon"}}>{profile.about}</p>
      <p className='lead'>{profile.work}</p>
      <ProfileButton/>
    </div>



    <div className='mt-4 ml-4 col-md-2'>


    </div>

    </div>
  )
}

export default Profile;
