import React,{useState,useEffect} from 'react'
import { useLocation } from 'react-router';
import AxiosRequest from '../../axios';
const Profile = () => {

  const location = useLocation();
  const key =window.location.pathname.slice(17,)


  const [profile,setProfile] = useState({about:"",id:"",adress:"",birthdate:"",followers:"",following:"",hobby:"",name:"",location:"",status:"",user:"",work:""});


  const retrieveProfileDetails = async() => {
    const details =await AxiosRequest.get(`/accounts/profile/${key}`)
                    .then((res) => {
                      console.log(res.data);
                      setProfile({about:res.data.about,id:res.data.id,adress:res.data.adress,birthdate:res.data.birthdate,followers:res.data.followers,following:res.data.following,hobby:res.data.hobby,name:res.data.name,location:res.data.location,status:res.data.status,user:res.data.user,work:res.data.work});
                    })
                    .catch(err => console.log(err))
  }

  useEffect(() => {
    retrieveProfileDetails()
  }, [])






  return (
    <div >
    <div align="center">
      <h1 className='display-5'>Profile Page</h1>
    </div>

    <div className='col-md-6'>
      <h1 className='display-6'>User Details: </h1>
      <p className='lead'>{profile.about}</p>
      <p className='lead'>{profile.adress}</p>
      <p className='lead'>{profile.birthdate}</p>
      <p className='lead'>{profile.hobby}</p>
      <p className='lead'>{profile.location}</p>
      <p className='lead'>{profile.status}</p>
      <p className='lead'>{profile.work}</p>
    </div>

    <div className='col-md-6'>
      <h1 className='diplay-6'></h1>
      <p className='lead'>{profile.id}</p>
      <p className='lead'>{profile.user}</p>

      <div className='d-flex'>
      <p className='lead'>{profile.followers}</p>
      <button className='btn btn-dark'></button>
      </div>

      <div className='d-flex'>
      <p className='lead'>{profile.following}</p>
      <button className='btn btn-dark'></button>
      </div>

      <p className='lead'>{profile.following}</p>
    </div>

    </div>
  )
}

export default Profile;
