import React from "react";
import prof from '../Images/prof.png'
import { Link } from "react-router-dom";


const ContactCard = (props) => {

    const {name,location,email,id} = props.elem.param;

    const passKey = () => {
        props.passKey(id);
    }


    return(
        <div className="item">
            <img className="ui avatar image" src={prof} alt=""/>
            <div className="content">
            <div className="header">{id}: <Link to='/details/${id}' state={props.elem.param}>{name}</Link></div>
            <div>{location}</div>
            <div>{email}</div>
            </div>
            <div style={{textAlign:"right"}}>
            <Link to='/delete'>
            <i className="trash alternative outline icon" onClick={passKey} style= {{color:"red", marginTop:"5px"}}></i> </Link>
            </div>
        </div>
    );
}
export default ContactCard;
