import React,{useState} from 'react'

import ExpenseItem from "./components/Expenses/ExpenseItem.js"
import "./app.css"
import Card from "./components/GeneralUI/card.js"
import CreateNew from "./components/NewExpenses/new.js"
import FilterYear from "./components/Expenses/filter.js"


const MainContent = (props) =>{

    const list_details = [
        {"date":new Date(2019, 6, 18),"type":"application submission","amt":50},
        {"date":new Date(2021, 8, 21),"type":"sallary withdwral","amt":150},
        {"date":new Date(2021, 4, 19),"type":"clent order","amt":200},
        {"date":new Date(2020, 3, 1),"type":"loan C instrest","amt":15},
        {"date":new Date(2020, 10, 25),"type":"Dinner Date","amt":180},
    ]

    const [data,setData] = useState(list_details);


    const saveData1 = (passData1) =>{
       setData([passData1,...data]);
    }





    const [year,setYear] = useState("2020");

    const valueYear = (passYear) =>{
        setYear(passYear);  

    }

    const filterData = data.filter(x => {
        return (x.date.toString().slice(11,15) === year.toString());
    })


    let NetData = <div class="mb-5 mt-5"><h3>No Data Found</h3></div>


    if (filterData.length > 0){
        NetData = filterData.map(element => 
            <ExpenseItem key={element.id}
                         type={element.type} 
                         date={element.date}  
                         amt={element.amt}/>)
    }


    return(
    <div align="center">
        <CreateNew getData1={saveData1}/>


        <Card align="center">
        <div><FilterYear getYear={valueYear} /></div>
            {NetData}
        </Card>

    </div>
    );
}

export default MainContent;
