import React,{useState,useEffect} from 'react'
import { DecodeToken } from '../token_decode'
import AxiosRequest from '../../axios'
import CartProductRecommendation from './CartProductRecommendation'

const CartDetails = () => {

    const user_id = DecodeToken(localStorage.getItem("access_token")).user_id;
    const [cartData,setCartData] = useState([])
    const [productRecommendation,setProductRecommendation] = useState();
    const [quantity,setQuantity] = useState();
    const cateogry = []

    const HandleRecommendation = () => {
        cartData.map((each) => {
            cateogry.push(each.cateogry);
        })
        const recc=  AxiosRequest.post(`/shareme/rec_post`,{data:cateogry})
                    .then((res) => {
                        console.log(res);



                    })
                    .catch((err) => {console.log(err)})

    }

    const retrieveCart = async() => {
        const cartData = await AxiosRequest.get(`/shareme/cart/${user_id}`)
                        .then((res) => {
                            setCartData(res.data);


                        })
                        .catch((err) => {console.log(err)})
    }

    const handleQuantity = async(id,price) => {
        const setQuantity = await AxiosRequest.put(`/shareme/cart/${id}`,{"quantity":quantity,"net_price":price})
                            .then((res) => {retrieveCart();})
                            .catch((err) => {console.log(err)})
    }

    const deleteCartItem = async(id) => {
        const deleteCartItem = await AxiosRequest.delete(`/shareme/cart/${id}`)
                            .then((res) => {retrieveCart();})
                            .catch((err) => {console.log(err)})
    }

    const RenderCartData = cartData.map((each) => {

        return(            
            <div style={{backgroundColor:"white", borderRadius:"25px",margin:"25px",padding:"25px"}} key={each.id} >
                <div className='d-flex' style={{justifyContent:"space-between"}}>
                <p style={{fontSize:"23px",textAlign:"left"}} className='lead'>{each.title}</p>
                <i className='large red close icon' onClick={() => {deleteCartItem(each.id)}}></i>
                </div>

                <div style={{textAlign:"left"}} className="row">
                <div className='col-md-6'>


                <p style={{fontSize:"16px"}}> Quantity: {each.quantity}</p>
                <p style={{fontSize:"16px"}}>NetPrice:{each.net_price}</p>
                </div>
                {/* <div className='col-md-6'>
                    <p style={{fontSize:"16px"}}>Change Quantity: </p>
                    <div className='d-flex' style={{gap:"20px"}}>
                    <input name={each.title} style={{borderColor:"black",borderWidth:"2px",width:"5rem",borderRadius:"15px"}} onChange={(e) => {setQuantity(parseInt(e.target.value))}}/>
                    <button className='btn btn-dark' onClick={ () => {handleQuantity(each.id,each.net_price)}}>Set</button>
                    </div>
                </div> */}
                </div>
            </div>



        );
    })


    useEffect(() => {
      retrieveCart();
    }, [])



    return (
    <div align="center">
        <h1 className='display-6'>Cart Details</h1>
        <div className='container row'>
        <div className='col-md-5'>
        {RenderCartData}
        </div>
        <div className='col-md-3'>

        </div>

        <div className='col-md-4' style={{textAlign:"right"}}>
            <CartProductRecommendation/>
        </div>

        </div>
    </div>
  )
}

export default CartDetails;
