import React,{useState} from 'react'


import "./form.css"


let InputData = "";


const Form= (props) =>{


    const [formtitle,setFormTitle] = useState("");
    const [formdate,setFormDate] = useState("");
    const [formamt,setFormAmt] = useState("");




    const handleTitle = (event) =>{
        return(setFormTitle(event.target.value));
    }

    const handleAmt = (event) =>{
        return(setFormAmt(event.target.value));
    }

    const handleDate = (event) =>{
        return(setFormDate(event.target.value));
    }

    const handleSubmit = (event) =>{


        event.preventDefault();
        if (formdate.length >0 && formtitle.length>0 && formamt.length>0){

            InputData = {"date":new Date(formdate),"type":formtitle,"amt":formamt};
            setFormAmt("");
            setFormDate("");
            setFormTitle("");
            props.getData(InputData);
        }


    }

    const [card,setCard1] = useState(false)

    const handleCancel = () =>{
        setCard1(false);
        console.log("cancel");
    }

    const handleCard = () => {
        setCard1(true);
    }

    const renderInput = 

            <form onSubmit={handleSubmit}>
            <div className="form_div1">
                <div className="form_title">
                <label for="text" class="form-label"><h4 >Title</h4></label>
                <input type="text" onChange={handleTitle} value={formtitle} class="form-control" placeholder="Enter a suitable title"/>
                </div>

                <div className="form_amt">
                <label for="number" class="form-label"><h4>Amount</h4></label>
                <input type="number" onChange={handleAmt} value={formamt} class="form-control"  placeholder="Enter the expense amount"/>
                </div>
                <div className="form_date" >
                <label class="form-label"><h4>Date</h4></label>
                <input type="date" onChange={handleDate} value={formdate} class="form-control" />
                </div>
            </div>

            <div className="form_div2">

                <button class="btn btn-dark" type="button" onClick={handleCancel} >Cancel</button>
                <button class="btn btn-dark" type="submit" onClick={handleSubmit} >Submit</button>

            </div>

            <div>{formtitle}{formdate}{formamt}</div>
            </form> 


    const dispBtn = <button class="btn btn-secondary mt-5 mb-5" type="button" onClick={handleCard} ><h3 class="display-5"> Add New Expenses</h3></button>;

    if (card===true){
        return(
            <div>{renderInput}</div>);
    }
    else{
        return(
            <div>{dispBtn}</div>);
    }    
}

export default Form;
