import React, { useEffect } from 'react'
import { useState } from 'react';

const FORMKEY = "form";


const UpdateNote = (props) => {

    const {topic,data,writer,id} = props.passData;


    const detailObject = {
        "topic":topic,
        "data":data,
        "writer":writer,
        "id":id,
    }


    const [formData,setFormData] = useState(detailObject);



    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(formData);

    }

    const handleClear = (event) => {
        detailObject.topic = topic;
        detailObject.writer = writer;
        detailObject.topic = data;

    }


    const storedForm = JSON.parse(localStorage.getItem("form"));
    if(storedForm){
       console.log(storedForm);
    }



    return (
        <div className="ui main">
        <form style={{margin:"20px",marginLeft:"20%",marginRight:"20%"}}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Update the Note</h2></div>
            <div className="ui form">
            <div className="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" defaultValue={formData.topic} onChange={(e) =>{detailObject.topic=e.target.value;setFormData(detailObject);}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea defaultValue={formData.data} onChange={(e) =>{detailObject.data=e.target.value;setFormData(detailObject);}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" defaultValue={formData.writer} onChange={(e) =>{detailObject.writer=e.target.value;setFormData(detailObject);}}></textarea>
            </div>
            </div>
            <button className="ui button black" onClick={handleClear} >Clear</button>
            <button className="ui button black" onClick={handleSubmit}>Submit</button>
        </form>
        </div>
    );

}
export default UpdateNote;
