import React from "react";
import prof from '../Images/prof.png'
import { Link } from "react-router-dom";


const ContactCard = (props) => {

    const {email,id,location,name} = props.elem.param;

    const passKey = () => {
        props.passKey(id);
    }

    return(
        <div className="item">


            <img className="ui avatar image" src={prof} alt=""/>


            <div className="content">

            <div className="header">{id}: <Link to={`/details/${id}`}>{name}</Link></div>
            <div>{location}</div>
            <div><Link to={{pathname:`/details/${id}`, state:{details: props.elem}}}>{email}</Link></div>
            </div>


            <div style={{textAlign:"right"}}>
            <i className="trash alternative outline icon" onClick={passKey} style= {{color:"red", marginTop:"5px"}}></i>  
            </div>



        </div>



    );
}

export default ContactCard;
