import React from "react";
import ContactCard from "./ContactCard";
import { Link } from "react-router-dom";
const ContactList = (props) => {


    const getKey = (para) =>{
        props.passKey(para);
    }




    const renderContacts = props.contacts.map((x) => {
        return(
            <ContactCard key={x.param.id}  elem={x} passKey={getKey} />
        );
    });
    return(
        <div>
        <br/>
           <br/>
           <br/>


               <h1> Contact List</h1>
               <div align="right">
               <Link to="/add">
               <button className="ui button black right">AddContact</button>
               </Link>
               </div>


           <br/>
        <div className="ui celled list">



           {renderContacts}


        </div>
        </div>
    );


}

export default ContactList;
