import React, {useState} from 'react'
import { useNavigate} from 'react-router'
// import GoogleLogin from 'react-google-login'
// import {FcGoogle} from 'react-icons/fc'
import { Link } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const CLIENT_ID = "1090350547929-lqosq4oc2pd2q7fnbn60tscegsdpgca6.apps.googleusercontent.com"

    const responseGoogle = (response) => {
        console.log(response);
        navigate("/");
    }

    const [loginDetails,setLoginDetails] = useState({username:"",password:""});

    const handleSubmit = () => {
        console.log(loginDetails);
        // navigate("/");
    }

    const handleClear = () => {

        loginDetails.username = ""
        loginDetails.password = ""

        setLoginDetails(loginDetails);
        setLoginDetails({username:"",password:""});


    }

    return (
        <div className="container col-md-5 mt-4">
        <h1>Enter your login details</h1>


        {/* <GoogleLogin 
          clientId= {CLIENT_ID}
          cookiePolicy={'single_host_origin'}
          buttonText='Login with Google'
          onSuccess={responseGoogle}
        /> */}


        <p className='lead'>New user <Link to='/signup'>click here</Link> to signup</p>

        <div className="mb-3 mt-3">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big user icon"></i>
        <p className="form-label lead">Username</p>
        </div>
        <input type="username" className="form-control" value={loginDetails.username} placeholder="Enter username" onChange={(e) => {setLoginDetails({username:e.target.value,password:loginDetails.password})}}/>
        </div>
        <div className="mb-3">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big keyboard icon"></i>
        <p className="form-label lead">Password</p>
        </div>
        <input type="password" className="form-control" value={loginDetails.password}  placeholder="Enter Password" onChange={(e) => {setLoginDetails({username:loginDetails.username,password:e.target.value})}}/>
        </div>
        <div align="center" className='d-grid gap-2'>
        <button type="button" className="btn btn-dark" onClick={handleSubmit}>Login</button>
        <button type="button" className="btn btn-danger" onClick={handleClear}>Clear</button>
        </div>
        </div>

    )
}

export default Login;
