import React,{useState} from "react";
import Header from "./components/header";
import NotesList from "./pages/NotesList";
import CreateNote from "./pages/CreateNote";


const App = () => {
    const [notesListArray,setNotesListArray] = useState([])
    console.log(notesListArray+"notelist array");


    const handleNewNote = (param) => {
        console.log(param);
        setNotesListArray([...notesListArray,{param}]);



    }

    return(
        <div>
            <div align="center" style={{margin:"30px",color:"purple"}}>
                <h1>NotesList React Application</h1>
            </div>

            <Header/>
            <NotesList/>
            <CreateNote passNote={handleNewNote}/>
        </div>
    );

}

export default App;
