import React from 'react'
import { useNavigate } from 'react-router';

const NoteCard = (props) => {


    let navigate = useNavigate();
    const {topic,author} = props.passNoteData;

    const handleDetails = () => {
        const detailsData = props.passNoteData;
        props.getDetails(detailsData);
        navigate("/details");

    }

    return ( 
       <div>
        <div className="ui card"  style={{margin:"15px",boxShadow: "2px 2px 1px grey",backgroundColor:"orange"}}>

       <div className="content" onClick={handleDetails} style={{"textAlign":"left"}}>
           <div className="header">{topic}</div>
           <div className="meta">

           <span className="date">Written By: {author} </span>
           </div>
       </div>
       </div>
        </div>

    )
}

export default NoteCard
