import React from 'react'
import { useState,useEffect } from 'react';
import Card from './card';
import Details from './details';
import Loading from '../basic/loading';
import { Link, useLinkClickHandler } from 'react-router-dom';
import AxiosRequest from '../../axios';

const Home = () => {

    const [postData,setPostData] = useState({loading:true,posts:[]});
    const [searchData,setSearchData] = useState("");
    const [postLimit,setPostLimit] = useState({start:0,end:5})

    const handleLimit = () => {
        setPostLimit({start:postLimit.end,end:postLimit.end+5})
    }




    const GetAPIData = async() => {
        const rawData = await AxiosRequest.post("shareme/posts",{search:searchData,...postLimit});
        co
        if(rawData.length === 0){
            setPostLimit({start:0,end:5})
        }
        else{
            setPostData({loading:false,posts:rawData.data});
        }

    }

    useEffect(() => {
        GetAPIData();   
    },[searchData,postLimit]);










    const RenderPostListData = postData.posts.map((each) => {
        return(
            <div key={each.id} style={{marginLeft:"3%",marginTop:"3%",marginRight:"3%",}}>
                <Card CardViewData={each}/>
            </div>
        );
    })
    if(postData.loading === false){
    return (
        <div className='container mt-3'>
        <div align="left" className='d-flex' style={{"gap":"25px",marginBottom:"25px"}}>
            <Link to='/shareme/posts_create'><i className='red big clone icon'></i></Link>

            <div className='d-flex' style={{alignItems:"center"}}>
            <div style={{display:"flex",alignItems:"center"}}>
                <input type="search"  class="form-control mr-2"  placeholder="Search" style={{width:"150px"}} onChange={(e) => {setSearchData(e.target.value)}} />

            </div>

            </div>

            </div>
        <div align="center" className='container' style={{"display":"grid","gridTemplateColumns": "repeat(auto-fit, minmax(200px, 1fr))"}}>
            {RenderPostListData}
        </div>
        <div style={{marginTop:"30px"}} align="center">
            <button class="btn btn-primary" onClick={handleLimit}>Load More</button>    
        </div>
        </div>
    )
    }
    else{
        return(
            <div>
                <Loading/>
            </div>
        );
    }
}

export default Home;
