import React,{ useState } from 'react'
import { Link,useNavigate } from 'react-router-dom';

const Login = () => {

    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");
    const [logState,setLogState] = useState(false);
    const [pass,setPass]  = useState(true);
    const [signView,setSign] = useState(false);



    const Lock = () => {
        return(
            <div class="ui icon warning message">
                <i class="lock icon"></i>
                <div class="content">
                    <div class="header">
                    Login failed!
                    </div>
                    <p>You might have misspelled your username or password!</p>
                </div>
                </div>

        );
    }


    let navigate = useNavigate();
    const handleLogin = (e) => {
        e.preventDefault();
        if(localStorage.getItem(username)===password){
            setLogState(true);
            navigate("/list");
        }
        else{
            console.log(pass);
            setPass(false);
        }
    }









    return (
        <div align="center">
        <div style={{display:"flex",justifyContent:"center",gap:"25px"}}>
            <h3>New user</h3> <button className="ui button black">Signup</button></div>
            <br/>
        <div class="page-login">
        <div class="ui centered grid container">
            <div class="nine wide column">
            {!pass && <Lock/>}
            <div class="ui fluid card">
                <div class="content">
                <form class="ui form">
                <div class="field">
                    <label>Username</label>
                    <input type="text" name="user" placeholder="User" onChange={(e) => {setUsername(e.target.value)}}/>
                </div>
                <div class="field">
                    <label>Password</label>
                    <input type="password" name="pass" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}}/>
                </div>
                <button class="ui primary labeled icon button" onClick={handleLogin}>
                    <i class="unlock alternate icon"></i>
                    Login
                </button>
                </form>
                </div>
            </div>
            </div>
        </div>
        </div>
        </div>
    )
}

export default Login;
